text:
Download the Raw Data
Set Parameters

raw_data_dir &lt;- here("data-raw")


if (!dir_exists(raw_data_dir)) {
  dir_create(raw_data_dir, recurse = TRUE)
}

Download File

osf_raw_data_id &lt;- "tdswp"


osf_raw_data_files &lt;-
  osf_raw_data_id |&gt;
  osf_retrieve_node() |&gt;
  osf_ls_files(
    type = "file",
    pattern = "rais_alim_",
    n_max = Inf
  )

osf_raw_data_files







raw_data_file &lt;-
  osf_raw_data_files |&gt;
  osf_download(path = raw_data_dir, conflicts = "overwrite") |&gt;
  extract2("local_path")

Unlock the File

raw_data_file &lt;-
  raw_data_file |&gt;
  unlock_file(
    private_key = private_key,
    suffix = ".lockr",
    remove_file = TRUE,
    password = password
  )
